<template>
    <transition :enter-active-class="enterActiveClass" :leave-active-class="leaveActiveClass" :mode="mode" :appear="appear">
        <slot></slot>
    </transition>
</template>

/*********************************************/
<!--animated  jello infinite-->
<script>
    import 'animate.css/animate.min.css'
    import AnimationType from './animation-type'

    export default {
        name: 'AnimationTransition',
        data () {
            return {
            };
        },
        props: {
            animationInType: {
                type: String,
                default: ''
            },
            animationOutType: {
                type: String,
                default: ''
            },
            mode: '',
            appear: false,
            speed: '',
            delay: '',
        },
        computed: {
            enterActiveClass() {
                if (!this.animationInType) return ''
                return `animated ${this.animationInType} ${this.speed} ${this.delay}`
            },
            leaveActiveClass() {
                if (!this.animationOutType) return ''
                return `animated ${this.animationOutType} ${this.speed} ${this.delay}`
            }
        },
    }
</script>

